//手机跳转
function browserRedirect() {
    var sUserAgent = navigator.userAgent.toLowerCase();
	var lHerf=location.href;
	var pupType1=lHerf.match(/utm_source/i) == "utm_source";
	var pupType2=lHerf.match(/bdjk/i) == "bdjk";
	var pupType3=lHerf.match(/bdwm/i) == "bdwm";
	var pupType4=lHerf.match(/bdkey=cc/i) == "bdkey=cc";
	
    var bIsIpad = sUserAgent.match(/ipad/i) == "ipad";
    var bIsIphoneOs = sUserAgent.match(/iphone os/i) == "iphone os";
    var bIsMidp = sUserAgent.match(/midp/i) == "midp";
    var bIsUc7 = sUserAgent.match(/rv:1.2.3.4/i) == "rv:1.2.3.4";
    var bIsUc = sUserAgent.match(/ucweb/i) == "ucweb";
    var bIsAndroid = sUserAgent.match(/android/i) == "android";
    var bIsCE = sUserAgent.match(/windows ce/i) == "windows ce";
    var bIsWM = sUserAgent.match(/windows mobile/i) == "windows mobile";
    if (bIsIphoneOs || bIsMidp || bIsUc7 || bIsUc || bIsAndroid || bIsCE || bIsWM) {
		if(pupType1 || pupType2 || pupType3 || pupType4){
       		window.location.href = 'http://m.ksnanke.com/#fromJJ';
		}else{
			window.location.href = 'http://m.ksnanke.com/#fromSEO';
		}
    }if(bIsIpad){
		var metaWidth = document.createElement("meta"); 
		metaWidth.setAttribute("name","viewport");
		metaWidth.setAttribute("content","width=1250, initial-scale=1.0, maximum-scale=1.0, user-scalable=no");
		var metaWidthBox=document.getElementsByTagName("head")[0];
		metaWidthBox.appendChild(metaWidth);
	} else {
        //window.location= 'http://www.ksnanke.com/'; 
    }
}
browserRedirect();

